  // 和 uniswap v2 进行交互  拿到swap信息
  // router2:  0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
  // factory:  0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f
  // testUSDC/WETH:  0xfa5db19087920f5d0e71d0373f099bd0c03589da
  // 拿到费率  汇率信息   比如 1个 WETH 能拿到 2665.64  个testUSDC
  // 参考 UniswapV2Router02.sol  方法 getAmountOut(amountIn, reserveIn, reserveOut)
  // 先通过 router2 拿到比率 也就是俩种token的比率  eth 换  dai
  // weth: 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2
  //  dai: 0x6B175474E89094C44Da98b954EedeAC495271d0F
  // 再预估 gas 费
  static Future<String> getSwapInfo() async{
    NetworkUtil.initialize();
    Network network = NetworkUtil.instances[1];

    print('network : ${network.name}');
    String tokenAHex = "0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2";
    String tokenBHex = "0x6B175474E89094C44Da98b954EedeAC495271d0F";

    var uniswapV2Router02 = DeployedContract(ContractAbi.fromJson(uniswapRouterV2Abi, "UniswapV2Router02"), EthereumAddress.fromHex("0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D"));
    var uniswapFactory = DeployedContract(ContractAbi.fromJson(uniswapFactoryAbi, "UniswapV2Factory"), EthereumAddress.fromHex("0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f"));

    EthereumAddress tokenA = EthereumAddress.fromHex(tokenAHex);
    EthereumAddress tokenB = EthereumAddress.fromHex(tokenBHex);

    // 1. 通过factory 拿到 pair 合约地址
    List<dynamic> _pairResult = [];
    await network.client.call(
        contract: uniswapFactory,
        function: uniswapFactory.function("getPair"),
        params: [tokenA, tokenB]
    ).then((value) => _pairResult = value);
    var pairAddress = _pairResult[0].toString();
    print('_pairResult: ${pairAddress}');

    // 2. 通过pair合约地址拿到俩种token的数量
    var uniswapV2Pair = DeployedContract(ContractAbi.fromJson(pairAbi, "UniswapV2Pair"), EthereumAddress.fromHex(pairAddress));
    List<dynamic> _reservesResult = [];
    List<dynamic> _token0Result = [];
    await Future.wait([
      network.client.call(
      contract: uniswapV2Pair,
      function: uniswapV2Pair.function("getReserves"),
      params: []
      ).then((value) => _reservesResult = value),

      network.client.call(
          contract: uniswapV2Pair,
          function: uniswapV2Pair.function("token0"),
          params: []
      ).then((value) => _token0Result = value)
    ]);

    print('_token0Result: ${_token0Result.toString()}');  // 0x6b175474e89094c44da98b954eedeac495271d0f
    print('_reservesResult: ${_reservesResult.toString()}'); //  [7737400515669789543117845, 4160326038950573824794, 1682264651]

    var token0 = _token0Result[0].toString();
    var reverse0 = _reservesResult[0].toString();
    var reverse1 = _reservesResult[1].toString();

    BigInt amount = BigInt.parse("1000");
    BigInt reserveIn, reserveOut;

    // 证明 顺序是对的  不需要换  in=weth   out=dai   tokenA=dai
    if(EthereumAddress.fromHex(token0).hexEip55 == tokenA.hexEip55){
        reserveIn = BigInt.parse(reverse0); // dai
        reserveOut = BigInt.parse(reverse1); // weth
    }else{
        reserveIn = BigInt.parse(reverse1);
        reserveOut = BigInt.parse(reverse0);
    }

    // 3. 通过 router2 去计算汇率
    List<dynamic> _rateResult = [];
    await network.client.call(
        contract: uniswapV2Router02,
        // getAmountOut(uint amountIn, uint reserveIn, uint reserveOut)
        function: uniswapV2Router02.function("getAmountOut"),
        params: [amount, reserveIn, reserveOut]
    ).then((value) => _rateResult = value);
    print('_rateResult: ${_rateResult.toString()}');

    // 4. 查看是否有余额

    var token = ERC20(address: tokenA, client: network.client);
    String pk = "0x761827f85f6b5cf3eaf3c5a8a930309438eec7950ebe2994302492a84b2124ed";  // account 2
    var credientials = EthPrivateKey.fromHex(pk);  // 私钥
    var balance = await token.balanceOf(credientials.address);
    print('your balance: $balance');
    if(balance > amount){
       print('u can continue');
    }else{
      print('balance not enough');
      return "";
    }

    // 5.通过 router2 去swap  ---   这个账号没钱  所以肯定走不通的
    List<dynamic> _swapResult = [];
    BigInt amountIn = amount;
    // 按照最低 4%的损失
    BigInt normalGet = BigInt.parse(_rateResult[0].toString());
    BigInt minGet = (normalGet * BigInt.from(996) / BigInt.from(1000)) as BigInt;
    BigInt amountOutMin = minGet;
    List<EthereumAddress> path = [];
    path.add(tokenA);
    path.add(tokenB);
    EthereumAddress to = EthereumAddress.fromHex("0x6f7F3E0Ff3bd4e6eCC50d2Ee60c38D28070116bD");  // account 2
    BigInt deadline = (DateTime.now().millisecondsSinceEpoch + 30*60*1000) as BigInt; // 30分钟
    await network.client.call(
        contract: uniswapV2Router02,
        /** uint amountIn,          //要换多少
            uint amountOutMin,      //最低多少
            address[] calldata path, //swap路径  必须存在且有流动性  比如 USDT->ETH->BNB   这是前端传进来的
            address to,              //接收者
            uint deadline            */
        function: uniswapV2Router02.function("swapExactTokensForTokens"),
        params: [amountIn, amountOutMin, path, to, deadline]
    ).then((value) => _swapResult = value);
    print('_swapResult: ${_swapResult.toString()}');

    return "";
  }
}